/**
 *	Primary navigation
 *
 *	@author Alexandre Masy
 **/
(function(window)
{

	/**
	 *	El constructor
	 **/
	function Primary()
	{
		this.view = jQuery('#primary');

		if ( this.view.length == 0 )
			return;

		this.view
				.on('mouseenter', onViewOver.bind(this))
				.on('mouseleave', onViewOut.bind(this))
		;

		this.win = jQuery(window);
		this.backgrounds = this.view.find('.backgrounds');
		this.animals = this.view.find('.animals');
		this.animalsItem = this.view.find('.animals .item');
		this.links = this.view.find('.navigation a').on('click', onLinkClick.bind(this));
		this.activator = this.view.find('.activator');

		this.currentIndex = 0;
		this.maxIndex = 5;
		this.itemHeight = 0;
		this.theme = 'home';

		this.updateLayout();

		// TweenMax.to( this.view, .3, {left:0, delay: .5});
		TweenMax.set( this.view, {left:0});
	}

	/**
	 *	Prototype
	 **/
	var p = Primary.prototype;

	/**
	 *	Set the current theme
	 *
	 *	@param value String
	 **/
	p.setTheme = function (value)
	{
		this.view.removeClass( this.theme ).addClass( value );
		this.theme = value;
	}

	/**
	 *	Define the progress
	 *
	 *	@param value number
	 **/	
	p.setProgress = function( value )
	{
		this.bgs.scrollTop( value );	
	}

	/**
	 *	Update the view
	 **/
	p.update = function()
	{
		this.updateLayout();
		this.setIndex( this.currentIndex );
	}

	/** 
	 *	Open the navigation
	 **/
	p.open = function()
	{
		if ( Layout.isSmall() )
			return;

		this.view.addClass('opened');
		helper.trigger( NavigationEvent.OPENED );
	}

	/**
	 *	Close the navigation
	 **/
	p.close = function()
	{
		if ( Layout.isSmall() )
			return;
		
		this.view.removeClass('opened');
		helper.trigger( NavigationEvent.CLOSED );
	}

	/**
	 *	Define the current index
	 *
	 *	@param value int
	 *	@param animate Boolean
	 **/
	p.setIndex = function( value, animate )
	{
		value = Math.max( 0, Math.min(this.maxIndex, value));

		var prevIndex = this.currentIndex;
		this.currentIndex = value;

		// links
		this.links.removeClass('active').eq(value).addClass('active');

		// activator
		var o = this.links.eq(value).position();
		var y = o.top + 9;
		TweenMax.to(this.activator, .3, {y: y});

		// backgrounds
		y = this.currentIndex * this.itemHeight; 
		TweenMax.to( this.backgrounds, 1, {scrollTo:{y: y}})
		
		// animals
		var oldone = this.animalsItem.eq(prevIndex)
		var newone = this.animalsItem.eq(this.currentIndex)

		oldone.removeClass('active');
		newone.addClass('active');

		// if ( this.animalTransition )
		// 	this.animalTransition.stop();
		
		// this.animalTransition = new TimelineMax()
		// 	.set( oldone, {className:"-=active"})
		// 	.set( newone, {height: 0, className:"+=active"})

		// 	.to( newone, 1, {height: 100})
		// 	.set( oldone, {height: 0})
		// ;
	}

	/**
	 *	Update the layout
	 **/
	p.updateLayout = function()
	{
		this.itemHeight = this.win.height();

		// // view
		// this.view.css({
		// 	height: this.itemHeight
		// })
	}

	/**
	 *	Mouse over handler
	 *
	 *	@param event MouseEvent
	 **/
	function onViewOver(event)
	{
		event.preventDefault();

		this.open();	

		return false;
	}

	/**
	 *	Mouse out handler
	 *
	 *	@param event MouseEvent
	 **/
	function onViewOut(event)
	{
		event.preventDefault();

		this.close();

		return false;
	}

	/**
	 *	Click on the links
	 *
	 *	@param event MouseEvent
	 **/
	function onLinkClick(event)
	{
		event.preventDefault();

		var el = jQuery(event.currentTarget);
		var href = el.attr('href');

		helper.trigger( ApplicationEvent.GO, {href:href} );

		return false;
	}
	

	
	helper.register( Primary, 'app.navigation.Primary' );
})(window);
(function(window)
{
	/**
	 *	AbstractPage
	 **/
	var AbstractPage = Class.extend({

		/**
		 *	Init the class
		 *	
		 *	@param selector String
		 *	@param index int
		 *	@param url String
	 	 *	@param theme String
		 **/
		init: function( selector, index, url, theme )
		{
			this.win = jQuery(window);
			this.view = jQuery(selector);
			this.index = index;
			this.url = url;
			this.theme = theme;
			this.content = this.view.find('.article-content');
			this.controls = this.view.find('.section-controls');
			this.view.find('.section-controls a').on('click', onControlsClick.bind(this))
			this.background = jQuery('#primary .backgrounds .'+theme);

			this.setupSection();
			setTimeout( this.updateLayout.bind(this), 300 );
		}

		/**	
		 *	Setup the sections
		 **/
		,setupSection: function()
		{
			var ColoredSection = helper.getClass('app.sections.ColoredSection');
			var ContentSection = helper.getClass('app.sections.ContentSection');
			var ListSection = helper.getClass('app.sections.ListSection');
			var FormSection = helper.getClass('app.sections.FormSection');
			var ExtraSection = helper.getClass('app.sections.ExtraSection');

			// first
			var view = this.view.find('.first');
			if ( view.length > 0 )
			{
				if ( view.hasClass('section-colored') )
					this.first = new ColoredSection(view);
				else if( view.hasClass('section-content') )
					this.first = new ContentSection(view); 
				else if( view.hasClass('section-list') )
					this.first = new ListSection(view); 
				else if( view.hasClass('section-form') )
					this.first = new FormSection(view);
				else if( view.hasClass('section-extra') )
					this.first = new ExtraSection(view);
			}

			// second
			view = this.view.find('.second');
			if ( view.length > 0 )
			{
				if ( view.hasClass('section-colored') )
					this.second = new ColoredSection(view);
				else if( view.hasClass('section-content') )
					this.second = new ContentSection(view); 
				else if( view.hasClass('section-list') )
					this.second = new ListSection(view); 
				else if( view.hasClass('section-form') )
					this.second = new FormSection(view);
				else if( view.hasClass('section-extra') )
					this.second = new ExtraSection(view);

				this.second.dispatcher.on( SectionEvent.OPEN, onSectionOpen.bind(this) )
			}

			// third
			view = this.view.find('.third');
			if ( view.length > 0 )
			{
				if ( view.hasClass('section-colored') )
					this.third = new ColoredSection(view);
				else if( view.hasClass('section-content') )
					this.third = new ContentSection(view); 
				else if( view.hasClass('section-list') )
					this.third = new ListSection(view); 
				else if( view.hasClass('section-form') )
					this.third = new FormSection(view);	
				else if( view.hasClass('section-extra') )
					this.third = new ExtraSection(view);	

				this.third.dispatcher.on( SectionEvent.CLOSE, onSectionClose.bind(this) )
			}
		}

		/**
		 *	Return the height of the view
		 *
		 *	@return Number
		 **/
		,height: function()
		{
			return this.viewHeight;
		}

		/**
		 *	Return the scroll min position
		 *	
		 *	@return Number
		 **/
		,scrollMin: function()
		{
			return this.view.offset().top;
		}

		/**
		 *	Return the scroll max position
		 *
		 *	@return Number
		 **/
		,scrollMax: function()
		{
			return this.height() + this.scrollMin();
		}

		/**
		 *	Start the wheel tracking
		 **/
		,start: function()
		{
			this.view.addClass('active');
		}

		/**
		 *	Stop the wheel tracking
		 **/
		,stop: function()
		{
			this.close();
			this.view.removeClass('active');
		}

		/**
		 *	Request the next page
		 **/
		,next: function()
		{
			helper.trigger(ApplicationEvent.NEXT);
			this.stop();
		}

		/**
		 *	Request the previous page
		 **/
		,previous: function()
		{
			helper.trigger(ApplicationEvent.PREVIOUS);
			this.stop();
		}

		/**
		 *	Open the sections
		 *	
		 *	@param url String
		 **/
		,open : function( url )
		{
			// request the page activation
			if (!this.view.hasClass('active'))
				helper.trigger( ApplicationEvent.GO, {href: this.url});

			// open the sections
			if (this.first)
				this.first.open();

			if (this.second)
				this.second.open();

			if (this.third)
				this.third.open(url);

			// activate the controls
			this.controls.addClass('active');
			this.updateLayout();
		}

		/**
		 *	Close the sections
		 **/
		,close: function()
		{
			if ( this.first )
				this.first.close();

			if ( this.second ) 
				this.second.close(); 

			if ( this.third )
				this.third.close(); 

			this.controls.removeClass('active');
			this.updateLayout();
		}

		/**
		 *	Update the section
		 **/
		,update: function()
		{
			if ( this.first )
				this.first.update();

			if ( this.second ) 
				this.second.update(); 

			if ( this.third )
				this.third.update(); 	
		}

		/**
		 *	Update the layout
		 **/
		,updateLayout: function()
		{
			var h = this.win.height();

			if ( this.first )
				h = Math.max( h, this.first.height() )

			if ( this.second ) 
				h = Math.max( h, this.second.height() );

			if ( this.third )
				h = Math.max( h, this.third.height() );

			if ( Layout.isSmall() )
				h = this.view.outerHeight();
			
			this.viewHeight = h;
			if ( Layout.isSmall() )
			{
				this.view.removeAttr('style');
			}
			else
			{
				this.view.css({height: h});
			}

			this.background.css({height: h});
		}
	});

	/**
	 *	Request the opening of a section handler
	 *	
	 *	@param event SectionEvent
	 *	@param params Object
	 **/
	function onSectionOpen( event, params )
	{
		this.open( params.url );
	}

	/**
	 *	Request the close of a section handler
	 *	
	 *	@param event SectionEvent
	 **/
	function onSectionClose( event )
	{
		this.close();
	}

	/**
	 *	Controls click 
	 *
	 *	@param event MouseEvent
	 **/
	function onControlsClick(event)
	{
		event.preventDefault();

		this.close();
		
		return false;
	}

	helper.register( AbstractPage, 'app.pages.AbstractPage' );
})(window);
